import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'


class WeatherApp extends React.Component {
    
    render() {
        return (
            <div className='app'>
                <main>
                    <div className ='search-box'>
                        <input type='text' className='search-bar' placeholder='Search by city name...'></input>
                    </div>
                    <div className = 'location-box'>
                        <div className = 'location'>NYC</div>
                        <div className = 'date'>{new Date().toLocaleDateString().slice(3,15)}</div>
                    </div>
                </main>
            </div>
        );
    }
}

ReactDOM.render(
    <WeatherApp/>,
    document.getElementById('root')
);
