import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'



// Helper function returning current day info
let dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day_full = days[d.getDay()];
    let day = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return day_full + ' ' + day + ' ' + month + ' ' + year;
}


function Weather(){
    return(
            <div>
                <div className='location-box'>
                    <div className='location'>NYC</div>
                    <div className='date'>{dateBuilder(new Date())}</div>
                </div>
                <div className='weather-box'>
                    <div className='temp'>
                        15
                    </div>
                    <div className='weather'>
                        Cloudy
                    </div>
                </div>
            </div>          
    )
}

class WeatherApp extends React.Component {

    constructor() {
        super()
        this.state = {
            input: []
        }
        this.handeChange = this.handleChange.bind
        
    }

    handeChange() {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    async searchCity() {
        const city = this.userInput;
        const result = await fetch(`http://localhost:4000/weather?city="${city}"`);
        const resultJson = await result.json();

        console.log(resultJson);

    }

    render() {
        return (
            <div className='app'>
                <main>
                    <div className='Greet'>Welcome to the coolest weather application!</div>
                    <label className='search-label'>Check the current weather conditions on a city of your choice!</label>
                    <div className='search-box'>                      
                        <input type='text' className='search-bar' value={this.userInput} placeholder='Search by city name, if you wish...'></input>
                        <button onClick={this.searchCity}>Search</button>
                    </div>
                    <div><Weather/></div>
                </main>
            </div>
        );
    }
}

ReactDOM.render(
    <WeatherApp />,
    document.getElementById('root')
);
